int compareIcons(const void * a, const void * b)
{
	char *v1 = (char *)a;
	char *v2 = (char *)b;
	
	char str1[63];
	char str2[63];
	
	strcpy(str1, v1);
	strcpy(str2, v2);
	
	strlwr(str1);
	strlwr(str2);
	
	return strcmp(str1, str2);	
}

int listIconSets(ICON_LIST buffer[])
{
	char tmpFile[256];
	int fType;
	
	strcpy(buffer[0].iconURL,"Default");
	uint16 pos = 1;

	DRAGON_chdir(d_icons);
	fType = DRAGON_FindFirstFile(tmpFile);
	
	while(fType != FE_NONE)
	{
		if(fType == FE_DIR)
		{
			if(strcmp(tmpFile,".") != 0 && strcmp(tmpFile,"..") != 0)
			{
				strcpy(buffer[pos].iconURL,tmpFile);
				pos++;
			}
		}
		
		fType = DRAGON_FindNextFile(tmpFile);
	}
	
	DRAGON_closeFind();
	DRAGON_chdir("/");
	
	if(pos > 1) // lets sort this list too
		qsort(buffer, pos, ICON_SIZE, compareIcons);
	
	return pos;
}

uint16 *loadIcon(char *path, uint16 *defIcon, uint16 defaultBG)
{
	int cX, cY;
	PICTURE_DATA iconData;
	char fileName[30];
	
	// run through and find the extension
	
	strcpy(fileName, path);
	strcat(fileName, ".bmp");
	
	if(DRAGON_FileExists(fileName) != FE_FILE)
	{
		strcpy(fileName, path);
		strcat(fileName, ".gif");
		
		if(DRAGON_FileExists(fileName) != FE_FILE)
		{
			strcpy(fileName, path);
			strcat(fileName, ".png");
			
			if(DRAGON_FileExists(fileName) != FE_FILE)
			{
				strcpy(fileName, path);
				strcat(fileName, ".jpg");
				
				if(DRAGON_FileExists(fileName) != FE_FILE)
				{
					strcpy(fileName, path);
					strcat(fileName, ".jpeg");
					
					if(DRAGON_FileExists(fileName) != FE_FILE) // there is no file with a proper extension
						return defIcon;
				}
			}
		}
	}
	
	if(!getPictureSize(fileName, cX, cY)) // couldn't open
		return defIcon;
	
	if(cX != (int)getSpriteWidth(defIcon) || cY != (int)getSpriteHeight(defIcon)) // invalid size for particular icon
		return defIcon;
	
	memset(&iconData, 0, sizeof(PICTURE_DATA));
	setBGColor(defaultBG, &iconData);
	disableGIFFileStreaming();
	setLargeDimensions(256,192); // normal thumbnail
	loadImage(fileName, &iconData, 0, 0);
	
	uint16 *tmpData = (uint16 *)safeMalloc((cX * cY * 2) + 4);
	memcpy(tmpData, iconData.picData, (cX * cY * 2) + 4);	
	
	freeImage(&iconData);
	
	return tmpData;
}

